&lt;meta http-eq'""" futures例子1234567891011121314151617181920212223242526272829303132333435363738394041424344import os,sys,time,requests,threadingfrom concurrent import futures URLS = [ 'http://baidu.com', 'http://www.qq.com', 'http://www.sina.com.cn' ] def load_url(url): print('tid:',threading.currentThread().ident,',url:',url) with requests.get(url) as resp: return resp.contentdef call_back(obj): print('-&gt;&gt;&gt;&gt;&gt;&gt;&gt;&gt;&gt;call_back , tid:',threading.currentThread().ident, ',obj:',obj) with futures.ThreadPoolExecutor(max_workers=3) as ex: # mp = &#123;ex.submit(load_url,url) : url for url in URLS&#125; mp = dict() for url in URLS: f = ex.submit(load_url,url) mp[f] = url f.add_done_callback(call_back) for f in futures.as_completed(mp): url = mp[f] try: data = f.result() except Exception as exc: print(exc, ',url:',url) else: print('url:', url, ',len:',len(data),',data[:20]:',data[:20])"""tid: 7128 ,url: http://baidu.comtid: 7892 ,url: http://www.qq.comtid: 3712 ,url: http://www.sina.com.cn-&gt;&gt;&gt;&gt;&gt;&gt;&gt;&gt;&gt;call_back , tid: 7892 ,obj: &lt;Future at 0x2dd64b0 state=finished returned bytes&gt;url: http://www.qq.com ,len: 251215 ,data[:20]: b'&lt;!DOCTYPE html&gt;
